function slowniePL(kwota) {
    const jednostki = ["", "jeden", "dwa", "trzy", "cztery", "piƒôƒá", "sze≈õƒá", "siedem", "osiem", "dziewiƒôƒá"];
    const nascie = ["dziesiƒôƒá", "jedena≈õcie", "dwana≈õcie", "trzyna≈õcie", "czterna≈õcie", "piƒôtna≈õcie", "szesna≈õcie", "siedemna≈õcie", "osiemna≈õcie", "dziewiƒôtna≈õcie"];
    const dziesiatki = ["", "", "dwadzie≈õcia", "trzydzie≈õci", "czterdzie≈õci", "piƒôƒádziesiƒÖt", "sze≈õƒádziesiƒÖt", "siedemdziesiƒÖt", "osiemdziesiƒÖt", "dziewiƒôƒádziesiƒÖt"];
    const setki = ["", "sto", "dwie≈õcie", "trzysta", "czterysta", "piƒôƒáset", "sze≈õƒáset", "siedemset", "osiemset", "dziewiƒôƒáset"];

    const grupy = [
        ["", "", ""],
        ["tysiƒÖc", "tysiƒÖce", "tysiƒôcy"],
        ["milion", "miliony", "milion√≥w"],
        ["miliard", "miliardy", "miliard√≥w"]
    ];

    function odmiana(liczba, formy) {
        if (liczba === 1) return formy[0];
        if ([2, 3, 4].includes(liczba % 10) && !(liczba % 100 >= 10 && liczba % 100 <= 20)) return formy[1];
        return formy[2];
    }

    function segment(n) {
        const s = Math.floor(n / 100);
        const d = Math.floor((n % 100) / 10);
        const j = n % 10;

        let wynik = [];

        if (s) wynik.push(setki[s]);
        if (d === 1) {
            wynik.push(nascie[j]);
        } else {
            if (d > 1) wynik.push(dziesiatki[d]);
            if (j > 0) wynik.push(jednostki[j]);
        }

        return wynik.join(" ");
    }

    function liczbaNaSlowa(n) {
        if (n === 0) return "zero";

        let res = [];
        let i = 0;

        while (n > 0) {
            let trzy = n % 1000;
            if (trzy > 0) {
                let seg = segment(trzy);
                if (i > 0) {
                    let formy = grupy[i];
                    let odm = odmiana(trzy, formy);
                    if (trzy === 1 && i === 1) {
                        seg = odm; // tylko "tysiƒÖc", bez "jeden"
                    } else {
                        seg += " " + odm;
                    }
                }
                res.unshift(seg);
            }
            n = Math.floor(n / 1000);
            i++;
        }

        return res.join(" ");
    }

    let zl = Math.floor(kwota);
    let gr = Math.round((kwota - zl) * 100);

    let wynik = liczbaNaSlowa(zl);// + " z≈Çotych";
    if (gr > 0) {
        wynik += " i " + liczbaNaSlowa(gr) + " groszy";
    }

    return wynik;
}



