for (const key in env1) {
    if (key.startsWith(prefix)) {
        const baseKey = key
            .substring(prefix.length)        // usuń prefix
            .toLowerCase()                   // małe litery
            .replace(/_/g, '_');             // opcjonalne, można zostawić jak jest
        msg.nocodb_t[baseKey] = env1[key];
    }
}

