const boundary = "----WebKitFormBoundary" + Math.random().toString(16).substr(2);

// Sprawdzenie, czy mamy bufor
if (!Buffer.isBuffer(fileObj.file)) {
    node.error("Plik nie jest Bufferem! Upewnij się, że został poprawnie załadowany z dysku.");
