    "Authorization": "Bearer " + global.get("chatgpt_api")
};

const crlf = "\r\n";
const bodyParts = [
    Buffer.from(`--${boundary}${crlf}` +
        `Content-Disposition: form-data; name="purpose"${crlf}${crlf}` +
        `assistants${crlf}`),

    Buffer.from(`--${boundary}${crlf}` +
        `Content-Disposition: form-data; name="file"; filename="${fileObj.filename}"${crlf}` +
        `Content-Type: image/png${crlf}${crlf}`),

    fileObj.file,

    Buffer.from(`${crlf}--${boundary}--${crlf}`)
];

msg.payload = Buffer.concat(bodyParts);

return msg;

msg.uploadResults.push(msg.payload.id);
