    const queuingCount = parseInt(match[1], 10);
    node.status({ fill: "gray", shape: "ring", text: queuingCount });
    const timestamp = global.get("getDateTime")();

    // Ustaw status węzła
    node.status({
        fill: queuingCount > 0 ? "yellow" : "green",
        shape: "dot",
        text: `${timestamp} | queuing: ${queuingCount}`
    });

    if (queuingCount == 0) {
        flow.set(processing_tag, false);
        msg.payload = "end";
        node.status({ fill: "gray", shape: "dot", text: "processing end" });
        return msg;
    }



    if (queuingCount > 0) {

        if (!processing) {
            flow.set(processing_tag, true);
            msg.topic = q.ct;
            msg.payload = "peek";
            node.status({ fill: "gray", shape: "dot", text: "od true" });
