function generateQRText(msg) {
    const NIP = "5671906227";  // <- Uzupełnij prawdziwym NIP-em odbiorcy
    const account_number = "02116022020000000371435981"; // <- 26 cyfr
    const account_number_clean = account_number.replace(/\s/g, '');

    const kwota_grosze = (kwota_platnosci || 0) * 100;
    const kwota_string = kwota_grosze.toFixed(0).padStart(6, '0'); // 6 cyfr z zerami wiodącymi

    const odbiorca_nazwa = "K&S Transport Sp. z o.o.";  // <- max 20 znaków
    const tytul = (msg.umowa.przelew_nazwa || "").substring(0, 32); // max 32 znaki

    const fields = [
        NIP,
        "PL",
        account_number_clean,
        kwota_string,
        odbiorca_nazwa.substring(0, 20),
        tytul,
        "", "", "" // rezerwa1, rezerwa2, rezerwa3
    ];

    return fields.join("|");
}

