    attachments: [],
    probably_contract: false,
    mail_delivery_failed: false,
    mail_delivery_failed_email: null,
    no_contract_found: false,
};

const bounce_patterns = [
    /550\s5\.1\.1/i,
    /Undelivered Mail Returned to Sender/i,
    /delivery[ -]status notification/i,
    /recipient address rejected/i,
    /mailbox unavailable/i
];

const failed_email_pattern = /<([^>]+@[^>]+)>/;

if (bounce_patterns.some(pattern => pattern.test(email.subject) || pattern.test(email.body))) {
    email_find.mail_delivery_failed = true;
    const failed_match = email.body?.match(failed_email_pattern);
    if (failed_match) {
        email_find.mail_delivery_failed_email = failed_match[1];
    }
}

// 1. Szukamy numeru umowy w temacie
let nr_umowy_match = email.subject?.match(/\b[A-Z]+-\d{1,}\/\d{1,}\b/);
if (nr_umowy_match) {
    email_find.nr_umowy = nr_umowy_match[0];
}

// 2. Szukamy UUID w treści
let uuid_match = email.body?.match(/uuid:([0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12})/i);
if (uuid_match) {
    email_find.uuid = uuid_match[1];
}

// 3. Parsowanie załączników
let raw_attachments = typeof email.attachments === "string" ? email.attachments.split(",") : [];

let found_any_pdf = false;
let found_pdf_with_nr_from_filename = false;
let found_image_non_barcode = false;

raw_attachments.forEach(path => {
    const filename = path.split("/").pop();
    if (filename === "barcode.png") return;

    const ext = filename.toLowerCase().split(".").pop();
    let kind = null;
    if (["pdf"].includes(ext)) kind = "pdf";
    else if (["jpg", "jpeg", "png", "bmp", "tiff", "webp"].includes(ext)) kind = "image";

    let nr_umowy_from_filename = null;
    let match_prefix = filename.match(/([A-Z])-(\d{1,})-(\d{1,})/);
    if (match_prefix) {
        nr_umowy_from_filename = `${match_prefix[1]}-${match_prefix[2]}/${match_prefix[3]}`;
        // jeśli nie było w mailu, a znaleźliśmy w nazwie pliku — ustawiamy
        if (!email_find.nr_umowy) {
            email_find.nr_umowy = nr_umowy_from_filename;
            email_find.nr_umowy_from_filename = nr_umowy_from_filename;
        }
        found_pdf_with_nr_from_filename = true;
    }

    if (kind === "pdf") {
        found_any_pdf = true;
    }

    if (kind === "image") {
        found_image_non_barcode = true;
    }

    email_find.attachments.push({
        patch: path,
        filename: filename,
        kind: kind,
        nr_umowy_from_filename: nr_umowy_from_filename
    });
});

// 4. Ustalenie probably_contract wg 3 zasad
if (
    (email_find.nr_umowy && found_any_pdf) ||      // nr umowy z maila + PDF
    (!nr_umowy_match && found_pdf_with_nr_from_filename) || // brak w mailu, ale nr w nazwie PDF
    (email_find.nr_umowy && found_image_non_barcode) // <-- nr umowy + zdjęcia
) {
    email_find.probably_contract = true;
}

node.status({ fill: "green", shape: "dot", text: global.get("getDateTime")() });
msg.email_find = email_find;
return msg;

