let raw_attachments = typeof email.attachments === "string" ? email.attachments.split(",") : [];

let found_pdf_with_nr = false;
let found_image_non_barcode = false;

raw_attachments.forEach(path => {
    const filename = path.split("/").pop();
    if (filename === "barcode.png") {
        // Pomijamy plik barcode.png
        return;
    }

    const ext = filename.toLowerCase().split(".").pop();

    let kind = null;
    if (["pdf"].includes(ext)) kind = "pdf";
    else if (["jpg", "jpeg", "png", "bmp", "tiff", "webp"].includes(ext)) kind = "image";

    let nr_umowy_from_filename = null;
    let match_prefix = filename.match(/^(\d{2})-(\d{2})_/);
    if (match_prefix) {
        nr_umowy_from_filename = `${match_prefix[1]}/${match_prefix[2]}`;
    }

    // warunki do oznaczenia jako prawdopodobna umowa
    if (kind === "pdf" && nr_umowy_from_filename === email_find.nr_umowy) {
        found_pdf_with_nr = true;
    }

    if (kind === "image") {
        found_image_non_barcode = true;
    }

    email_find.attachments.push({
        patch: path,
        filename: filename,
        kind: kind,
        nr_umowy: nr_umowy_from_filename
    });
});

