const path = global.get("path");
const PDFDocument = global.get("pdfkit");  // dodaj do settings.js je≈õli nie ma
const A4_LANDSCAPE = [841.89, 595.28];

const path_storage = global.get("storage");
const path_umowy = global.get("path_umowy");
const path_email = global.get("path_email");

const attachments = msg.email_find.attachments || [];
const umowa_folder = `${path_storage}${path_umowy}${msg.umowa.umowa_year}/${msg.umowa.umowa_nr}/`;
const email_folder = `${path_storage}${path_email}`;
const path1 = msg.umowa.path_umowa_orginalna;
msg.source_file = path.basename(path1);

if (!fs.existsSync(umowa_folder)) {
    fs.mkdirSync(umowa_folder, { recursive: true });
}

const copied_files = [];

for (const att of attachments) {
    const src = path.join(email_folder, att.patch); // np. /zenflowai/storage/inbox/attachment/27/image0.jpeg
    const dest = path.join(umowa_folder, att.filename);

    if (fs.existsSync(src)) {
        fs.copyFileSync(src, dest);
        copied_files.push(dest);
    } else {
        node.warn(`Plik nie istnieje: ${src}`);
        node.status({ fill: "red", shape: "dot", text: `Plik nie istnieje: ${src}` });

    }
}

// Tworzenie PDF z obrazk√≥w
if (copied_files.length > 0) {
    const pdfPath = path.join(umowa_folder, "podpisana-e-"+msg.source_file);
    const doc = new PDFDocument({ autoFirstPage: false });

    const writeStream = fs.createWriteStream(pdfPath);
    doc.pipe(writeStream);

    for (const imgPath of copied_files) {
        // const { width, height } = doc.openImage(imgPath);
        // doc.addPage({ size: [width, height] });
        // doc.image(imgPath, 0, 0);

        // const img = doc.openImage(imgPath);
        // const width = img.width;
        // const height = img.height;

        // doc.addPage({ size: [width, height] });
        // doc.image(img, 0, 0, { width, height: height - 1 }); // delikatna korekta

        const img = doc.openImage(imgPath);
        doc.addPage({ size: A4_LANDSCAPE });

        doc.image(img, 0, 0, {
            fit: A4_LANDSCAPE,
            align: 'center',
            valign: 'center'
        });

    }

    doc.end();

    msg.umowa.path_pdf_zalaczniki = pdfPath;
    msg.payload = {
        status: "ok",
        pdf: pdfPath,
        kopie: copied_files
    };
    node.status({ fill: "green", shape: "dot", text: pdfPath });
} else {
    msg.payload = { status: "brak plik√≥w do kopiowania" };
    node.status({ fill: "red", shape: "dot", text: "brak plik√≥w do kopiowania" });
}

return msg;

