const bwipjs = global.get('bwip-js');
const fs = global.get('fs');
const path = global.get('path');

// Funkcja pomocnicza do daty w formacie: YYYYMMDDHHMMSS
const getTimestamp = () => {
    const now = new Date();
    const pad = (n) => n.toString().padStart(2, '0');
    return `${now.getFullYear()}${pad(now.getMonth() + 1)}${pad(now.getDate())}${pad(now.getHours())}${pad(now.getMinutes())}${pad(now.getSeconds())}`;
};

// Tworzymy unikalnƒÖ nazwƒô pliku
const filename = `barcode-${getTimestamp()}.png`;
const filepath = `/zenflowai/storage/tmp/${filename}`;

bwipjs.toBuffer({
    bcid: 'qrcode',
    text: '123456789012',
    scale: 4,
    // height: 4,
    includetext: true,
    textxalign: 'center',
}, function (err, png) {
    if (err) {
        node.error("B≈ÇƒÖd generowania barcode", err);
    } else {
        fs.writeFileSync(filepath, png);

        msg.payload = png.toString('base64');
        msg.barcode_filename = filename;
        msg.barcode_path = filepath;
        msg.barcode_mime = "image/png";

        return node.send(msg);
    }
});

